// src/pages/Login.js
import React, { useState, useContext, useEffect } from 'react';
import { AuthContext } from '../context/AuthContext';
import { useNavigate } from 'react-router-dom';

const Login = () => {
  const { login, isAuthenticated, error: authError } = useContext(AuthContext);
  const navigate = useNavigate();

  const [formData, setFormData] = useState({
    email: '',
    password: '',
  });
  const [error, setError] = useState('');

  const { email, password } = formData;

  // This effect will run whenever isAuthenticated or authError changes.
  useEffect(() => {
    // If login becomes successful, navigate to the home page.
    if (isAuthenticated) {
      navigate('/');
    }

    // If the context provides an error, display it.
    if (authError) {
      setError(authError.msg || 'Invalid Credentials');
    }
  }, [isAuthenticated, authError, navigate]);

  const onChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
    // Clear previous errors when user starts typing again
    setError('');
  };

  const onSubmit = (e) => {
    e.preventDefault();
    if (email === '' || password === '') {
      setError('Please fill in all fields');
      return;
    }
    // Call the login action from the context
    login(formData);
  };

  return (
    <div className="form-container">
      {/* Error Popup */}
      {error && (
        <div className="popup error">
          <p>{error}</p>
        </div>
      )}

      <h1>
        Account <span style={{ color: '#0a66c2' }}>Login</span>
      </h1>
      <form onSubmit={onSubmit}>
        <div className="form-group">
          <label htmlFor="email">Email Address</label>
          <input
            type="email"
            name="email"
            value={email}
            onChange={onChange}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="password">Password</label>
          <input
            type="password"
            name="password"
            value={password}
            onChange={onChange}
            required
          />
        </div>
        <input
          type="submit"
          value="Login"
          className="btn btn-primary btn-block"
        />
      </form>
    </div>
  );
};

export default Login;
