// src/context/AuthContext.js
import React, { createContext, useReducer, useEffect } from 'react';
import api from '../api';

// Initial state
const initialState = {
  token: localStorage.getItem('token'),
  isAuthenticated: null,
  loading: true,
  user: null,
  error: null,
};

// Create context
export const AuthContext = createContext(initialState);

// Reducer
const authReducer = (state, action) => {
  const { type, payload } = action;

  switch (type) {
    case 'USER_LOADED':
      return {
        ...state,
        isAuthenticated: true,
        loading: false,
        user: payload,
      };
    case 'LOGIN_SUCCESS':
      localStorage.setItem('token', payload.token);
      return {
        ...state,
        ...payload,
        isAuthenticated: true,
        loading: false,
        error: null,
      };
    case 'REGISTER_SUCCESS':
        // On register, we don't log the user in. We just prepare for login.
      return {
        ...state,
        loading: false,
        error: null,
      };
    case 'AUTH_ERROR':
    case 'LOGOUT':
      localStorage.removeItem('token');
      return {
        ...state,
        token: null,
        isAuthenticated: false,
        loading: false,
        user: null,
        error: payload,
      };
    default:
      return state;
  }
};

// Provider component
export const AuthProvider = ({ children }) => {
  const [state, dispatch] = useReducer(authReducer, initialState);

  // This function will run once when the app loads
  const loadUser = async () => {
    const token = localStorage.getItem('token');
    if (token) {
      try {
        const res = await api.get('/auth');
        dispatch({
          type: 'USER_LOADED',
          payload: res.data,
        });
      } catch (err) {
        dispatch({ type: 'AUTH_ERROR' });
      }
    } else {
        // Set loading to false if there's no token
        dispatch({ type: 'AUTH_ERROR' });
    }
  };
  
  useEffect(() => {
    loadUser();
  }, []);

  // Register User
  const register = async (formData) => {
    try {
      await api.post('/auth/register', formData);
      dispatch({ type: 'REGISTER_SUCCESS' });
    } catch (err) {
      dispatch({ type: 'AUTH_ERROR', payload: err.response.data });
    }
  };

  // Login User
  const login = async (formData) => {
    try {
      const res = await api.post('/auth/login', formData);
      dispatch({
        type: 'LOGIN_SUCCESS',
        payload: res.data,
      });
      // After a successful login dispatch, immediately try to load the user data.
      // This ensures the user object is populated and isAuthenticated is definitively true.
      await loadUser(); 
    } catch (err) {
      dispatch({ type: 'AUTH_ERROR', payload: err.response.data });
    }
  };

  // Logout
  const logout = () => dispatch({ type: 'LOGOUT' });

  return (
    <AuthContext.Provider
      value={{
        ...state,
        register,
        login,
        logout,
      }}
    >
      {children}
    </AuthContext.Provider>
  );
};
