// src/pages/Home.js
import React, { useContext, useEffect } from 'react';
import { PostContext, PostProvider } from '../context/PostContext';
import { AuthContext } from '../context/AuthContext';
import PostForm from '../components/posts/PostForm';
import PostItem from '../components/posts/PostItem';
import Spinner from '../components/layout/Spinner';
import { useNavigate } from 'react-router-dom';

const HomeContent = () => {
    const { posts, loading, getPosts } = useContext(PostContext);
    const { isAuthenticated } = useContext(AuthContext);
    const navigate = useNavigate();

    useEffect(() => {
        if (!isAuthenticated) {
            navigate('/login');
        } else {
            getPosts();
        }
    }, [isAuthenticated, getPosts, navigate]);

    if (loading) return <Spinner />;

    return (
        <div>
            <h1 style={{ color: '#0a66c2' }}>Home Feed</h1>
            <p>Welcome to the community!</p>
            <PostForm />
            <div className="posts">
                {posts.map((post) => (
                    <PostItem key={post._id} post={post} />
                ))}
            </div>
        </div>
    );
};

// We wrap the content in the provider so it has access to the post context
const Home = () => (
    <PostProvider>
        <HomeContent />
    </PostProvider>
);

export default Home;
