// src/pages/Register.js
import React, { useState, useContext, useEffect } from 'react';
import { AuthContext } from '../context/AuthContext';
import { useNavigate } from 'react-router-dom';

const Register = () => {
  const { register, isAuthenticated } = useContext(AuthContext);
  const navigate = useNavigate();

  // State for the form fields
  const [user, setUser] = useState({
    name: '',
    email: '',
    password: '',
    password2: '',
  });
  // State to control the success popup
  const [showSuccess, setShowSuccess] = useState(false);
  // State for password mismatch error
  const [error, setError] = useState('');

  const { name, email, password, password2 } = user;

  useEffect(() => {
    // If user is already logged in, redirect to home
    if (isAuthenticated) {
      navigate('/');
    }
  }, [isAuthenticated, navigate]);

  const onChange = (e) => setUser({ ...user, [e.target.name]: e.target.value });

  const onSubmit = (e) => {
    e.preventDefault();
    // Reset previous errors
    setError('');

    if (password !== password2) {
      setError('Passwords do not match');
    } else {
      register({ name, email, password });
      // Show the success message
      setShowSuccess(true);
      // Wait 2 seconds, then redirect to the login page
      setTimeout(() => {
        navigate('/login');
      }, 2000);
    }
  };

  return (
    <div className="form-container">
      {/* Success Popup */}
      {showSuccess && (
        <div className="popup success">
          <p>Registration successful! Redirecting to login...</p>
        </div>
      )}

      {/* Error Popup */}
      {error && (
        <div className="popup error">
          <p>{error}</p>
        </div>
      )}

      <h1>
        Account <span style={{ color: '#0a66c2' }}>Register</span>
      </h1>
      <form onSubmit={onSubmit}>
        <div className="form-group">
          <label htmlFor="name">Name</label>
          <input type="text" name="name" value={name} onChange={onChange} required />
        </div>
        <div className="form-group">
          <label htmlFor="email">Email Address</label>
          <input type="email" name="email" value={email} onChange={onChange} required />
        </div>
        <div className="form-group">
          <label htmlFor="password">Password</label>
          <input type="password" name="password" value={password} onChange={onChange} required minLength="6" />
        </div>
        <div className="form-group">
          <label htmlFor="password2">Confirm Password</label>
          <input type="password" name="password2" value={password2} onChange={onChange} required minLength="6" />
        </div>
        <input type="submit" value="Register" className="btn btn-primary btn-block" />
      </form>
    </div>
  );
};

export default Register;
